import React, {useEffect} from 'react'
import {HeaderTitle} from '../../../components/HeaderTitle/HeaderTitle'
import {Button, Card} from '@material-ui/core'
import styled from 'styled-components'
import {ls} from '../../../tools/dealKey'
import {useStoreModel} from '../../../ModelAction/useStore'
import {meModel} from '../model'

const MyCard = styled(Card)`
  margin: 40px 20px;
  padding: 10px 20px;
  display: flex;
  flex-direction: column;
  > section {
    display: flex;
    height: 30px;
    align-items: center;
    > aside {
      margin-right: 10px;
    }
    > footer {
      > button {
        padding: 2px 8px;
      }
    }
  }
`

export const MyInfo = () => {
  const {state: stateMe, actions: actionsMe} = useStoreModel(meModel)
  useEffect(() => {
    if (!stateMe.user.id) {
      actionsMe.getLoginUser()
    }
  }, [])

  return <div>
    <HeaderTitle
        title={'我的达人证'}
    />
    <MyCard>
      <section>
        <aside>
          {ls('姓名')}
        </aside>
        <main>{stateMe.user.userInfo?.name}</main>
        <footer>
          <Button
              variant={'outlined'}
          >{ls('编辑')}</Button>
        </footer>
      </section>
      <section>
        <aside>
          {ls('电话')}
        </aside>
        <main>{stateMe.user.userInfo?.phone}</main>
      </section>
      <section>
        <aside>
          {ls('邮箱')}
        </aside>
        <main>{stateMe.user.userInfo?.email}</main>
      </section>
    </MyCard>
  </div>
}
