import React, {useEffect} from 'react'
import {useRouter} from 'next/router'
import {HeaderTitle} from '../../../components/HeaderTitle/HeaderTitle'
import {modelFactory} from '../../../ModelAction/modelUtil'
import {CategoryItemInput, Product} from '../../../graphqlTypes/types'
import {doc} from '../../../graphqlTypes/doc'
import {fpMergePre} from '../../../tools/utils'
import {useStoreModel} from '../../../ModelAction/useStore'

export const CategoryPageModel = modelFactory('CategoryPage', {
  productList: [] as Product[],
}, {
  getProductList: async (value: CategoryItemInput, option) => {
    const res = await option.query(doc.productsInCategory, value)
    option.setData(fpMergePre({
      productList: res?.productsInCategory,
    }))
    console.log(res)
  },

})

export const CategoryPage = () => {
  const router = useRouter()
  const id = (router.query?.id as string) ?? ''
  const {state: stateCPM, actions: actionsCPM} = useStoreModel(CategoryPageModel)

  useEffect(() => {
    if (router.query.id) {
      actionsCPM.getProductList()
    }
  }, [router.query.id])


  return <div>
    <HeaderTitle
        title={''}
    />
    {id}
    Category
  </div>
}
