import React from 'react'
import {HeaderTitle} from '../../../components/HeaderTitle/HeaderTitle'
import {SigninInput, SigninLabel} from '../../register'
import {ls} from '../../../tools/dealKey'
import {modelFactory} from '../../../ModelAction/modelUtil'
import {useStoreModel} from '../../../ModelAction/useStore'
import {fpSetPre} from '../../../tools/utils'
import styled from 'styled-components'

const updatePasswordModel = modelFactory('updatePasswordModel', {
  form: {
    oldPassword: '',
    newPassword: '',
    confirmPassword: '',
  },
}, {
  setForm: ([path, value], option) => {
    option.setData(fpSetPre(`form.${path}`, value))
  },

})

const Box = styled.div`
  padding: 0 20px;
  ${SigninInput} {
    margin-top: 20px;
  }
`

export const UpdatePassword = () => {
  const {state: stateUPM, actions: actionsUPM} = useStoreModel(updatePasswordModel)

  return <div>
    <HeaderTitle
        title={'修改密码'}
    />
    <Box>
      <SigninLabel>{ls('原始密码')}</SigninLabel>
      <SigninInput
          value={stateUPM.form.oldPassword}
          onChange={event => actionsUPM.setForm(['name', event.target.value])}
      />

      <SigninInput
          label={ls('新密码')}
          value={stateUPM.form.newPassword}
          onChange={event => actionsUPM.setForm(['newPassword', event.target.value])}
      />
      <SigninLabel>{ls('确认密码')}</SigninLabel>
      <SigninInput
          value={stateUPM.form.confirmPassword}
          onChange={event => actionsUPM.setForm(['confirmPassword', event.target.value])}
      />
    </Box>
    UpdatePassword
  </div>
}
