const imgDomain = process.env.img_domain ?? 'http://127.0.0.1:4464/'

export const dealImgUrl = (src: string | undefined | null = '') => {
  if (src?.includes('blob:')) {
    return src
  }
  if (process.env.NODE_ENV === 'production') {
    return `${src}`
  }
  return `${imgDomain}${src}`
}
