import React from 'react'
import {modelFactory} from '../../../../ModelAction/modelUtil'
import {useStoreModel} from '../../../../ModelAction/useStore'
import {fpMergePre} from '../../../../tools/utils'
import {SigninInput} from '../../../register'
import {FieldContain} from '../../myInfo/updatePassword'
import {HeaderTitle} from '../../../../components/HeaderTitle/HeaderTitle'
import {UserPayCard} from '../../../../graphqlTypes/types'
import {setForm} from '../../../../tools/commonAction'
import {ls} from '../../../../tools/dealKey'
import {useRouter} from 'next/router'

export const myCreditCardEditModel = modelFactory('myCreditCardEditModel', {
  form: {
    number: '',
    code: '',
    name: '',
    userName: '',
    addressDetail: '',
    zipCode: '',
    city: '',
    contact: '',
  } as UserPayCard,
}, {
  setForm: setForm,
  clearForm: (value, option) => option.setData(fpMergePre({
    form: {
      number: '',
      code: '',
      name: '',
      userName: '',
      addressDetail: '',
      zipCode: '',
      city: '',
      contact: '',
    },
  })),
  submit: (value, option) => {

  },
})

export const MyCreditCardEdit = () => {
  const router = useRouter()
  const id = router.query?.id ?? ''
  console.log(id)
  const {state: stateMCCE, actions: actionsMCCE} = useStoreModel(myCreditCardEditModel)

  return <div>
    <HeaderTitle
        title={'编辑信用卡'}
        backCall={actionsMCCE.clearForm}
    />
    <FieldContain>
      {[
        ['信用卡号', 'number'],
        ['四位号码', 'code'],
        ['姓名', 'userName'],
        ['详细地址', 'addressDetail'],
        ['邮政编码', 'zipCode'],
        ['城市', 'city'],
        ['联系方式', 'contact'],
      ].map(v => <SigninInput
          key={`MyCreditCardEdit_${v[1]}`}
          label={ls(v[0])}
          value={stateMCCE.form[v[1] as keyof UserPayCard]}
          onChange={event => actionsMCCE.setForm(['number', event.target.value])}
      />)}

    </FieldContain>
  </div>
}
