    />
    <CusCarousel
        height={'240px'}
        dataList={product?.img?.map(v => ({
          ...v,
          imgUrl: v?.url,
        })) as []}
    />
    <PriceRed>
      <main>
        {ls('基准价格')}
        <span>{dealMoney(product.priceOut)}/{product.packingUnitString}</span>
      </main>
      <aside>{ls('已成团')}{stateGroupProduct.groupQueueList.filter(v => v.sumFillAmount === product?.groupPrecision).length}{ls('单')}</aside>
