    </PriceRed>
    <Name>
      <main>{product.name}</main>
      <section>{product.groupRemark}/{product.groupAmount}{product.groupAmountUnitString}<br/>{ls('分团精度')}
        <span>{[...Array(product.groupPrecision)].map((v, i) => i).map(value =>
            <YellowStar key={value}/>)}</span>
      </section>
    </Name>
    <GroupQueueBox>
      <Title>{ls('拼团中')}</Title>
      {stateGroupProduct.groupQueueList.map(groupQueue => <GroupQueueListBox>
        <aside>
          {[...Array(product.groupPrecision)].map((v, i) => i).map(value => value + 1 > (groupQueue.sumFillAmount ?? 0) ?
              <StarBorderRoundedIcon
                  key={`clickStar${value}`}
                  fontSize={'large'}
                  onClick={() => actionsGroupProduct.updateSelectNum(value + 1)}
              /> : <StarRoundedIcon
                  key={`clickStar${value}`}
