import React, {useEffect} from 'react'
import ChevronRightIcon from '@material-ui/icons/ChevronRight'
import {HeaderTitle} from '../../components/HeaderTitle/HeaderTitle'
import {useStoreModel} from '../../ModelAction/useStore'
import {pageTypeEnum, shopCartModel} from './index'
import styled from 'styled-components'
import {ls} from '../../tools/dealKey'
import {PickUpTypeEnum} from '../../ss_common/enum'
import {doc} from '../../graphqlTypes/doc'
import {IconButton, LinearProgress} from '@material-ui/core'
import {BScroller} from '../../components/BScroll/BScroller'
import {Space} from '../../components/Box/Box'

const Title = styled.div`
  font-size: 18px;
  font-weight: bold;
`
const AddressBox = styled.div`
  padding: 20px;
  display: flex;
  align-items: center;
  > main {
    flex-grow: 1;
    > header {
      font-size: 16px;
      font-weight: bold;
      margin-bottom: 8px;
    }
  }
`

export const OrderPage = () => {
  const {state: stateSCM, actions: actionsSCM, getLoad} = useStoreModel(shopCartModel)

  useEffect(() => {
    if (!stateSCM.user.id) {
      actionsSCM.getOrderInfo()
    }
  }, [])

  const addressData = ((stateSCM.form.pickUpType === PickUpTypeEnum.Delivery && stateSCM.userAddressList) || stateSCM.selfAddress).find(v => v.id === stateSCM.form.addressId)
  console.log(addressData)

  return <div>
    <HeaderTitle
        title={'确认订单'}
        backCall={() => {
          actionsSCM.updatePageType(pageTypeEnum.shopCart)
          return true
        }}
    />
    {(!!getLoad(doc.orderConfirmInfo) && <LinearProgress/>) || <div style={{height: '4px'}}/>}
    <BScroller
        boxHeight={'calc(100vh - 65px)'}
    >
      <Space h={10}/>
      <Title>
        <Space w={20}/>
        {ls((stateSCM.form.pickUpType === PickUpTypeEnum.Self && '自取地址') || '送货地址')}
      </Title>
      <AddressBox>
        <main>
          <header>
            {(stateSCM.form.pickUpType === PickUpTypeEnum.Self && `${addressData.province} ${addressData.city} ${addressData.streetAddress}`) || `${addressData.combineAddress}`}
          </header>
          <footer>
            {(stateSCM.form.pickUpType === PickUpTypeEnum.Self && `${addressData.fullName} ${addressData.phone}`) || `${addressData.name} ${addressData.contactInformation}`}
          </footer>
        </main>
        <aside>
          <IconButton>
            <ChevronRightIcon/>
          </IconButton>
        </aside>
      </AddressBox>
    </BScroller>
  </div>
}
