import React from 'react'
import {Carousel} from 'react-responsive-carousel'
import {dealImgUrl} from '../../tools/img'
import {makeStyles, styled} from '@material-ui/styles'

type CarouselProps = {
  height?: string,
  dataList: any[],
  onClickItem?: (index: number, item: React.ReactNode) => void,
  renderItem?: (item: any) => React.ReactChild,
  showArrows?: boolean,
  showIndicators?: boolean,
  showThumbs?: boolean,
}

const useCusCarouselStyle = makeStyles({
  box: {}
})
const CarouselBox = styled(Carousel)({
})

const CusCarousel = (
    {
      dataList,
      onClickItem,
      height,
      renderItem,
      showArrows = false,
      showIndicators = true,
      showThumbs = false,
    }: CarouselProps) => {
  const classes = useCusCarouselStyle()
  return (
      <Carousel
          autoPlay={true}
          showThumbs={showThumbs}
          showArrows={showArrows}
          showStatus={false}
          showIndicators={showIndicators}
          infiniteLoop={true}
          onClickItem={onClickItem}
          className={classes.box}
      >
        {renderItem ? dataList?.map((item) => renderItem(item))
            : dataList?.map((item) => (
                <div
                    key={`Carousel_${item.id}`}
                >
                  <img
                      style={{height: height}}
                      src={dealImgUrl(item.imgUrl)}
                      alt=""/>
                </div>
            ))}
      </Carousel>
  )
}

export default CusCarousel
