import styled from 'styled-components'
import React from 'react'
import {Product} from '../../graphqlTypes/types'
import {dealImgUrl} from '../../tools/img'
import {Card} from '@material-ui/core'
import {dealMoney} from '../../tools/utils'

const Box = styled(Card)`
  padding: 10px;
  > header {
    width: 100%;
    height: 40vw;
    > img {
      border-radius: 8px;
      width: 100%;
      height: 40vw;
    }
  }
  > main {
    margin-top: 5px;
  }
  > footer {
    font-size: 14px;
  }
`

export const ProductItem = ({product}: { product: Product }) => {
  console.log(product.img)
  return <Box>
    <header>
      <img src={dealImgUrl(product?.img?.[0]?.url)}
           alt=""/>
    </header>
    <main>{product.name}{product.weight}{product.unit}</main>
    <footer>
      {dealMoney(product.priceMarket)}
      <span
      >{dealMoney(product.priceOut)}</span>
    </footer>
  </Box>
}
